import React from 'react';
import './App.css';
import Images from './Images';
import Loading from './Loading';


class App extends React.Component {

  constructor(){
    super();
    this.state={
      loading:true
    }
  }

  render(){
  return (

    <div className="App">
      <h1 className="f1 i">Infinite Scroll Images</h1>
      { this.state.loading?
      <Loading/> :
      <Images/>
      }
    </div>
  );
}
}
export default App;
