import React from 'react';
import './App.css';
import 'tachyons';
import Images from './Images';
import Loading from './Loading';

const apiKey ='gZltUiUKrujp2xyoVwrqyrnolWRivEF1UvOnHqg4Dz8';
const count =10;
const url=`https://api.unsplash.com/photos/random/?client_id=${apiKey}&count=${count}`;
async function getPhotos(){
  try{
    const response= await fetch(url);
    const data= await response.json();
    console.log(data);

  }catch(error){
    console.log("error..something wrong with fetching!..");
  }
}

class App extends React.Component {

  constructor(){
    super();
    this.state={
      loading:false
    }
  }

  componentDidMount(){
    //getPhotos();
  }
  render(){
  return (

    <div className="App">
      <h1 className=" ">Infinite Scroll Images</h1>
      { this.state.loading?
      <Loading/> :
      <Images/>
      }
    </div>
  );
}
}
export default App;
