import React from 'react';
import './App.css';
import 'tachyons';
import Images from './Images';
import Loading from './Loading';

const apiKey ='gZltUiUKrujp2xyoVwrqyrnolWRivEF1UvOnHqg4Dz8';
const count =10;
const url=`https://api.unsplash.com/photos/random/?client_id=${apiKey}&count=${count}`;

const ww = window;

class App extends React.Component {

  constructor(){
    super();
    this.state={
      loading:false,
      photosArray:[],
      ready:true,
    }
  }

  getPhotos =async function(){
    try{
      const response= await fetch(url);
      const data= await response.json();
      this.setState({photosArray:data});
      console.log(this.state.photosArray);


    }catch(error){
      console.log("error..something wrong with fetching!..");
    }
  }

  componentDidMount(){
    this.getPhotos();
    window.addEventListener('scroll',()=>{
      if(window.innerHeight + window.scrollY >= document.body.offsetHeight - 1000 && this.state.ready)
      {
        this.setState({ready:false});
        this.getPhotos();
        console.log("loaadingggg..")

      }

    });
  }
  render(){
  return (

    <div className="App">
      <h1 className=" ">Infinite Scroll Images</h1>
      { this.state.loading?
      <Loading/> :
      <Images array={this.state.photosArray}/>
      }
    </div>
  );
}
}
export default App;
